sys_ui_action.script:
var msgCount = 0;
var dupCount = 0;
var toLanguage = current.current_languages.id;
var toLanguageX = toLanguage + "X";

translateMessages();


//add tranlation information to log record
current.message_log = msgCount + " messages translated from English to " + current.current_languages.name;
answer = current.insert();
gs.include('ActionUtils');
var au = new ActionUtils();
au.postInsert(current);

gs.addInfoMessage(msgCount + ' messages translated');
if (dupCount > 0) {
	gs.addInfoMessage(dupCount + ' duplicate records skipped');
}


function translateMessages() {                           //  Read the sys_ui_message table                            
	var gr = new GlideRecord('sys_ui_message');
	gr.addQuery('language', 'en');

	if (current.update_custom_messages_only) {           // If custom only selected .. add to the query
		gr.addQuery('sys_customer_update', 'true');
		gr.setLimit(current.custom_messages); 
		gs.addInfoMessage(' Custom messages only ' + current.custom_messages);
	}

	gr.query();                                          // loop thru the messages to tranlate
	while (gr.next()) {
		gs.addInfoMessage(' in loop');
		translateRec(gr);
		msgCount++;
	  }
 }


function translateRec(currRec) {
	  var gt = new global.GoogleTranslate();
      var trMessage = gt.translate(toLanguage, currRec.message, "en");   //convert message

	  var newRec = new GlideRecord('sys_ui_message');
	  newRec.initialize();
	  newRec.key = currRec.key;
	  newRec.application = currRec.application;
	  newRec.language = toLanguageX;                        // language 'X' until approved
	  newRec.sys_customer_update = 'true';
	  newRec.message = trMessage;
	 try {
		  newRec.insert();	 
	 }
	 catch(err) {
		 dupCount++;
	 } 


}
